mapper.delete:
delete from ana_account


    sid = #{sid}


    and login_name = #{loginName}


    and mobile = #{mobile}


    and email = #{email}

mapper.select:
select count(1)
from ana_account


    sid = #{sid}


    and login_name = #{loginName}


    and login_name like concat(#{loginNameLike},'%')


    and real_name like concat(#{realNameLike},'%')


    and mobile = #{mobile}


    and mobile like concat(#{mobileLike},'%')


    and email = #{email}


    and gender = #{gender}


    and type = #{type}


    and status = #{status}


    and company_info_sid = #{companyInfoSid}


    and sid in
    (
    #{searchingSid, jdbcType=BIGINT}
  )

select 
from ana_account


    sid = #{sid}


    and login_name = #{loginName}


    and login_name like concat(#{loginNameLike},'%')


    and real_name like concat(#{realNameLike},'%')


    and mobile = #{mobile}


    and mobile like concat(#{mobileLike},'%')


    and email = #{email}


    and gender = #{gender}


    and type = #{type}


    and status = #{status}


    and company_info_sid = #{companyInfoSid}


    and sid in
    (
    #{searchingSid, jdbcType=BIGINT}
  )



  limit #{offset}, #{limit}
