mapper.delete:
delete from ana_company_info


    sid = #{sid}


    and code = #{code}

mapper.select:
select count(1)
from ana_company_info


    sid = #{sid}


    and code = #{code}


    and type = #{type}


    and contact_name like concat(#{contactNameLike},'%')


    and contact_mobile like concat(#{contactMobileLike},'%')


    and status = #{status}

select 
from ana_company_info


    sid = #{sid}


    and code = #{code}


    and type = #{type}


    and contact_name like concat(#{contactNameLike},'%')


    and contact_mobile like concat(#{contactMobileLike},'%')


    and status = #{status}



  limit #{offset}, #{limit}
