mapper.select:
select 
from wf_process_execution


    sid = #{sid}


    and process_sid = #{processSid}


    and process_owner = #{processOwner}


    and owner_sid = #{ownerSid}


    and process_inst_id = #{processInstanceId}


    and process_status = #{processStatus}


    and current_step_sid = #{currentStepSid}


    and active_flag = #{activeFlag}


    and init_by = #{initBy}


order by init_time desc

select count(1) from
(select 
from wf_process_execution

  assigned_type = 'role'

    and sid = #{sid}


    and process_owner = #{processOwner}


    and owner_sid = #{ownerSid}


    and process_sid = #{processSid}


    and process_inst_id = #{processInstanceId}


    and process_status = #{processStatus}


    and current_step_sid = #{currentStepSid}


    and assigned_to in
    (
    #{assignedToRole, jdbcType=VARCHAR}
  )


    and active_flag = #{activeFlag}


    and checked_by = #{checkedBy}


     and checked_time >= #{checkedTimeFrom} 


     and checked_time <= #{checkedTimeTo} 


     and init_time >= #{initTimeFrom} 


     and init_time <= #{initTimeTo} 


union all
select 
from wf_process_execution

  assigned_type = 'individual'

    and sid = #{sid}


    and process_owner = #{processOwner}


    and owner_sid = #{ownerSid}


    and process_sid = #{processSid}


    and process_inst_id = #{processInstanceId}


    and process_status = #{processStatus}


    and current_step_sid = #{currentStepSid}


    and assigned_to = #{assignedToIndividual}


    and active_flag = #{activeFlag}


    and checked_by = #{checkedBy}


     and checked_time >= #{checkedTimeFrom} 


     and checked_time <= #{checkedTimeTo} 


     and init_time >= #{initTimeFrom} 


     and init_time <= #{initTimeTo} 

) as tdl

select * from
(select 
from wf_process_execution

  assigned_type = 'role'

    and sid = #{sid}


    and process_owner = #{processOwner}


    and owner_sid = #{ownerSid}


    and company_info_sid = #{companyInfoSid}


    and process_inst_id = #{processInstanceId}


    and process_status = #{processStatus}


    and current_step_sid = #{currentStepSid}


    and assigned_to in
    (
    #{assignedToRole, jdbcType=VARCHAR}
  )


    and active_flag = #{activeFlag}


    and checked_by = #{checkedBy}


     and checked_time >= #{checkedTimeFrom} 


     and checked_time <= #{checkedTimeTo} 


     and init_time >= #{initTimeFrom} 


     and init_time <= #{initTimeTo} 


union all
select 
from wf_process_execution

  assigned_type = 'individual'

    and sid = #{sid}


    and process_sid = #{processSid}


    and process_owner = #{processOwner}


    and owner_sid = #{ownerSid}


    and process_inst_id = #{processInstanceId}


    and process_status = #{processStatus}


    and current_step_sid = #{currentStepSid}


    and assigned_to = #{assignedToIndividual}


    and active_flag = #{activeFlag}


    and checked_by = #{checkedBy}


     and checked_time >= #{checkedTimeFrom} 


     and checked_time <= #{checkedTimeTo} 


     and init_time >= #{initTimeFrom} 


     and init_time <= #{initTimeTo} 

) as tdl
order by tdl.checked_time desc

  limit #{offset}, #{limit}
